mapper.select:
select sum(pays) sumPrice,count(1) sumNum, sum(case when taskstate=4 then pays else 0 end) as sumFinishPrice, sum(case when taskstate=3 then pays else 0 end) as sumDeliveryPrice, sum(case when taskstate=2 then pays else 0 end) as sumOrderPrice from t_my_task_detail t,t_my_task m where 1=1 and t.mytaskid = m.id and t.create_by=#{map.userid}
and m.state = #{map.state}
and (t.create_date BETWEEN #{map.create1} and #{map.create2})

select DATE_FORMAT(t.create_date,'%Y-%m-%d') countCreateDate,b.buyerName,b.loginName,b.userid, sum(pays) sumPrice,count(1) sumNum, sum(case when taskstate=4 then 1 else 0 end) as sumFinishNum, sum(case when taskstate=4 then pays else 0 end) as sumFinishPrice, sum(case when taskstate=3 then pays else 0 end) as sumDeliveryPrice, sum(case when taskstate=2 then pays else 0 end) as sumOrderPrice from t_my_task_detail t,t_buyer_info b where 1=1 and t.buyerid = b.userid
and b.buyerName like #{map.buyerName}
and (t.create_date BETWEEN #{map.createDate1} and #{map.createDate2}) group by DATE_FORMAT(t.create_date,'%Y-%m-%d'),b.buyerName,b.loginName,b.userid order by DATE_FORMAT(t.create_date,'%Y-%m-%d')
