mapper.select:
	  select t.*,s.soldlogin username,si.loginname shopname from t_shop_role_user t left join t_shop_info si on t.shopid=si.userid left join t_sold_info s
      on t.userid=s.userid


		    ${ew.sqlSegment}

select DATE_FORMAT(tb.effectdate,'%Y-%m-%d') effectdate,s.shopname,s.loginname,sum(tb.tasknum)-sum(case when tb.status=2 then tb.canreceivenum else 0 end) tasknum, sum(tb.tasknum)-sum(case when tb.status=2 then tb.canreceivenum else 0 end)-sum(finishcount) unanswerednum,sum(case when tb.status=2 then 0 else tb.canreceivenum end) canreceivenum, sum(finishcount) finishnum,(sum(tb.tasknum)-sum(case when tb.status=2 then tb.canreceivenum else 0 end)-sum(finishcount))*${map.multiple} unansweredPrice,sum(finishcount)*${map.multiple} finishPrice from t_shop_role_user t left join t_shop_info s on t.shopid=s.userid left join t_task_base tb on t.shopid=tb.shopid left join (select taskid,sum(case when taskstate='4' then 1 else 0 end) finishcount from t_my_task_detail m,t_task_base b,t_shop_role_user s where m.taskid=b.id and b.shopid=s.shopid and s.userid=#{map.userid} group by taskid) my on tb.id=my.taskid where 1=1 and t.userid=#{map.userid} and (tb.effectdate BETWEEN #{map.createDate1} and #{map.createDate2})
and s.loginname like #{map.loginname}
and s.shopname like #{map.shopname}
group by DATE_FORMAT(tb.effectdate,'%Y-%m-%d'),s.shopname,s.loginname order by DATE_FORMAT(tb.effectdate,'%Y-%m-%d')
