mapper.select:
SELECT
FROM T_sold_Info t inner join sys_user u on u.id=t.userid inner join t_seller_level sltb on sltb.id=t.accountLevel
${ew.sqlSegment}

mapper.update:
update
  t_logistics_order_detail t
set
  t.status=#{map.status},t.update_by=#{map.updateBy},t.update_date=#{map.updateTime}
where
  t.master_id=#{map.masterId}
